            console.log(data.data)
            this.setState(
                {
                    stage: data.data.root
                }
            )
        })
    }

    getLocation() {
        if (navigator.geolocation) {
            navigator.geolocation.getCurrentPosition(this.getCoordinates);
        }
    }

    getCoordinates(position) {
        console.log(position.coordinates)
    }

    nextSlide(slide) {
        this.setState(
            {
                stage: slide
            }
        )
    }

    render() {
        return (
            <main className='page'>
                <News />
