import React from 'react';
import './Intro.scss';

const Intro = (props) => {

    if (props.showButton) {
        return (
            <div className='intro'>
                <h2 className='intro__story'>{props.intro}</h2>
                <h3 className='intro__button'>NEXT</h3>
            </div>
        )
    } else {
        return (
        <div className='intro'>
            <h2 className='intro__story'>{props.intro}</h2>
        </div>
        )
    }
}

export default Intro;
