import React from 'react';
import './display.scss';
import Slide from '../components/Slide/Slide';
import Complete from '../components/Complete/Complete';
import BackgroundVideo from '../components/BackgroundVideo/BackgroundVideo';

class Display extends React.Component {

    state = {
        stage: '',
        finish: false
    }

    componentDidMount() {
        this.setState({
            stage: this.props.story.tree.root
        })
        console.log(this.props.story.audio)
        this.audio = new Audio('http://localhost:8080/public/audio/Wind.wav');
        this.audio.load();
        this.playAudio();
    }

    componentWillUnmount() {
        this.audio.pause();
    }

    playAudio() {
        const audioPromise = this.audio.play()
        if (audioPromise !== undefined) {
          audioPromise
            .then(_ => {
              // autoplay started
            })
            .catch(err => {
              // catch dom exception
              console.info(err)
            })
        }
    }

    nextSlide(slide) {
        if (slide.right) {
            this.setState(
                {
                    stage: slide
                }
            )
        } else {
            this.setState(
                {
                    finish: true
                }
            )
        }
    }

    render() {
        if (!this.state.finish) {
            return (
                <div>
                    <BackgroundVideo story={this.props.story}><Slide story={this.state.stage} nextHandler={(slide) => this.nextSlide(slide)}/></BackgroundVideo>
                </div>
            )
        } else {
            return <Complete change={this.props.change}/>
        }
    }
}

export default Display;
