import React from 'react';
import Slide from '../components/Slide/Slide';
import axios from 'axios';
import './main.scss';

class Main extends React.Component {

    state = {
        stage: {}
    }

    componentDidMount() {
        axios.get('http://localhost:8080/scenarios').then(data => {
            console.log(data.data)
            this.setState(
                {
                    stage: data.data.root
                }
            )
        })
    }

    nextSlide(slide) {
        this.setState(
            {
                stage: slide
            }
        )
    }

    render() {
        return (
            <main className='page'>
                <Slide story={this.state.stage} nextHandler={this.nextSlide.bind(this)}/>
                <div className='page__alert'>1</div>
                <div className='page__task'>click here</div>
            </main>
        )
    }
}

export default Main;
