import React from 'react';
import './display.scss';
import Slide from '../components/Slide/Slide';

class Display extends React.Component {

    nextSlide(slide) {
        this.setState(
            {
                stage: slide
            }
        )
    }

    render() {
        return (
            <Slide story={this.props.story} nextHandler={(slide) => this.nextSlide(slide)}/>
        )
    }
}

export default Display;
