import React from 'react';
import './BackgroundVideo.scss';

const BackgroundVideo = (props) => {
    if (!props.story.background) {
        console.log(props)
        return null
    } else if (props.story.background.includes('.jpg')) {
        let image = 'http://localhost:8080/public/videos/'+props.story.background;
        <div className='video' style={{background: `url(${image})`}}>
            {props.children}
        </div>
    }
    else {
        return (
            <div className='video'>
                {props.children}
                <div className='video__container'>
                    <video autoPlay='autoplay' loop='loop' muted>
                        <source src={'http://localhost:8080/public/videos/'+props.story.background} type='video/mp4'/>
                    </video>
                </div>
            </div>
        )
    }
}

export default BackgroundVideo;
