import React from 'react';
import './display.scss';
import Slide from '../components/Slide/Slide';
import Complete from '../components/Complete/Complete';
import BackgroundVideo from '../components/BackgroundVideo/BackgroundVideo';

class Display extends React.Component {

    componentDidMount() {
        this.audio = new Audio('http://localhost:8080/public/audio/'+this.props.story.audio);
        this.audio.load();
        this.audio.loop = true;
        this.playAudio();
    }

    componentWillUnmount() {
        this.audio.pause();
    }

    playAudio() {
        const audioPromise = this.audio.play()
        if (audioPromise !== undefined) {
          audioPromise
            .then(_ => {
              // autoplay started
            })
            .catch(err => {
              // catch dom exception
              console.info(err)
            })
        }
    }

    render() {
        if (!this.state.finish) {
            return (
                <div>
                    <BackgroundVideo story={this.props.story}><Slide story={this.state.stage} nextHandler={(slide) => this.nextSlide(slide)}/></BackgroundVideo>
                </div>
            )
        } else {
            return <BackgroundVideo story={this.props.story}><Complete story={this.state.stage} change={this.props.change}/></BackgroundVideo>
        }
    }
}

export default Display;
