import React from 'react';
import './display.scss';
import Slide from '../components/Slide/Slide';
import Complete from '../components/Complete/Complete';
import BackgroundVideo from '../components/BackgroundVideo/BackgroundVideo';
import SlideController from '../controller/SlideController';
import Intro from '../components/Intro/Intro';

class Display extends React.Component {

    state={
        intro: true,
        showButton: false
    }

    componentDidMount() {
        console.log('I mounted')
        this.audio = new Audio('http://localhost:8080/public/audio/'+this.props.story.audio);
        this.audio.load();
        this.audio.loop = true;
        this.playAudio();

        setTimeout(() => {
            this.setState({
                showButton: true
            })
        }, 6000)
    }

    componentWillUnmount() {
        this.audio.pause();
    }

    componentDidUpdate(prevProps) {
        if (prevProps.story !== this.props.story) {
            console.log('here at updated');
            this.audio.pause();
            this.audio = new Audio('http://localhost:8080/public/audio/'+this.props.story.audio);
            this.audio.load();
            this.audio.loop = true;
            this.playAudio();

            this.setState({
                intro: true,
                showButton: false
            })

            setTimeout(() => {
                this.setState({
                    showButton: true
                })
            }, 6000)
        }
    }

    playAudio() {
        const audioPromise = this.audio.play()
        if (audioPromise !== undefined) {
          audioPromise
            .then(_ => {
              // autoplay started
            })
            .catch(err => {
              // catch dom exception
              console.info(err)
            })
        }
    }

    startStory() {
        this.setState({
            intro: false
        })
    }

    render() {
        console.log(this.props.story)
        if (this.state.intro) {
            return <Intro showButton={this.state.showButton} intro={this.props.story.intro} startStory={() => this.startStory()}/>
        } else {
            return <BackgroundVideo story={this.props.story}><SlideController lose={this.props.lose} story={this.props.story} change={this.props.change} nextStory={this.props.nextStory}/></BackgroundVideo>  
        }
    }
}

export default Display;
